class EvmDisassemblerTest(unittest.TestCase):

    def setUp(self):
        self.testcode = ''

    def test_disassembler(self):
        disassembler = EvmDisassembler()
        instruction_list = list(disassembler.disassemble(self.testcode))
        self.assertTrue(instruction_list)
        self.assertIsInstance(instruction_list[0], Instruction)
        self.assertIsInstance(instruction_list[-1], Instruction)
        self.assertEqual(instruction_list[0].name, "PUSH1")

        self.assertEqual(''.join(disassembler.assemble(instruction_list)), self.testcode)

    def test_disassembler_0xinput(self):
        disassembler = EvmDisassembler()
        instruction_list = list(disassembler.disassemble("0x"+self.testcode))
        self.assertTrue(instruction_list)
        self.assertIsInstance(instruction_list[0], Instruction)
        self.assertIsInstance(instruction_list[-1], Instruction)
        self.assertEqual(instruction_list[0].name, "PUSH1")

        self.assertEqual(''.join(disassembler.assemble(instruction_list)), self.testcode)


class MyInstruction(Instruction):

    def __init__(self, opcode, name, length_of_operand=0, description=None, args=None, returns=None, gas=-1,
                 category=None, pops=None, pushes=None, fork=None):
        super().__init__(opcode=opcode, name=name,
                         length_of_operand=length_of_operand,
                         description=description,
                         args=args, returns=returns,
                         gas=gas, category=category,
                         pops=pops, pushes=pushes, fork=fork)

        # additional attribs
        self.annotations = []
        self.xrefs = set([])
        self.jumpto = None
        self.basicblock = None


class EvmDisassemblerCustomTest(unittest.TestCase):

    def setUp(self):
        self.registry = registry.InstructionRegistry(instructions=registry.INSTRUCTIONS, _template_cls=MyInstruction)
        self.testcode = ''

    def test_disassembler(self):
        disassembler = EvmDisassembler(_registry=self.registry)
        instruction_list = list(disassembler.disassemble(self.testcode))
        self.assertTrue(instruction_list)
        self.assertIsInstance(instruction_list[0], Instruction)
        self.assertIsInstance(instruction_list[-1], Instruction)
        self.assertEqual(instruction_list[0].name, "PUSH1")

        self.assertEqual(''.join(disassembler.assemble(instruction_list)), self.testcode)

        for instr in instruction_list:
            self.assertIsInstance(instr, MyInstruction)

    def test_disassembler_0xinput(self):
        disassembler = EvmDisassembler()
        instruction_list = list(disassembler.disassemble("0x"+self.testcode))
        self.assertTrue(instruction_list)
        self.assertIsInstance(instruction_list[0], Instruction)
        self.assertIsInstance(instruction_list[-1], Instruction)
        self.assertEqual(instruction_list[0].name, "PUSH1")

        self.assertEqual(''.join(disassembler.assemble(instruction_list)), self.testcode)

